// N8N Health Monitoring System
const healthChecks = {
  database_connectivity: null,
  api_endpoints: [],
  workflow_performance: {},
  resource_usage: {},
  error_rates: {},
  sla_compliance: {}
};

// Database connectivity check
try {
  // This would normally make an actual DB connection
  healthChecks.database_connectivity = {
    status: 'healthy',
    response_time_ms: Math.random() * 50 + 10,
    last_checked: new Date().toISOString()
  };
} catch (error) {
  healthChecks.database_connectivity = {
    status: 'unhealthy',
    error: error.message,
    last_checked: new Date().toISOString()
  };
}

// API endpoint health checks
const endpoints = [
  { name: 'supabase', url: 'https://ktchrfgkbpaixbiwbieg.supabase.co/rest/v1/' },
  { name: 'perplexity', url: 'https://api.perplexity.ai/chat/completions' },
  { name: 'claude', url: 'https://api.anthropic.com/v1/messages' },
  { name: 'openai', url: 'https://api.openai.com/v1/chat/completions' }
];

endpoints.forEach(endpoint => {
  // Simulate health check (in real implementation, make actual HTTP calls)
  const isHealthy = Math.random() > 0.1; // 90% healthy simulation

  healthChecks.api_endpoints.push({
    name: endpoint.name,
    url: endpoint.url,
    status: isHealthy ? 'healthy' : 'unhealthy',
    response_time_ms: isHealthy ? Math.random() * 200 + 50 : null,
    last_checked: new Date().toISOString(),
    error_count_24h: isHealthy ? Math.floor(Math.random() * 3) : Math.floor(Math.random() * 20) + 5
  });
});

// Workflow performance metrics
const workflows = [
  'pharmaceutical-intake',
  'ai-content-generation', 
  'compliance-review',
  'fda-data-integration'
];

workflows.forEach(workflow => {
  healthChecks.workflow_performance[workflow] = {
    executions_24h: Math.floor(Math.random() * 100) + 20,
    success_rate: 0.85 + Math.random() * 0.14, // 85-99% success rate
    avg_execution_time_ms: Math.random() * 30000 + 5000, // 5-35 seconds
    failed_executions_24h: Math.floor(Math.random() * 5),
    last_execution: new Date(Date.now() - Math.random() * 3600000).toISOString()
  };
});

// Resource usage monitoring
healthChecks.resource_usage = {
  cpu_usage_percent: Math.random() * 40 + 30, // 30-70%
  memory_usage_percent: Math.random() * 50 + 25, // 25-75%
  disk_usage_percent: Math.random() * 30 + 20, // 20-50%
  active_workflows: workflows.length,
  queue_size: Math.floor(Math.random() * 10),
  concurrent_executions: Math.floor(Math.random() * 5) + 1
};

// Error rate analysis
healthChecks.error_rates = {
  total_errors_24h: Math.floor(Math.random() * 15) + 2,
  critical_errors_24h: Math.floor(Math.random() * 3),
  error_rate_percent: (Math.random() * 5) + 1, // 1-6%
  most_common_errors: [
    'API rate limit exceeded',
    'Database connection timeout',
    'Invalid submission format'
  ]
};

// SLA compliance monitoring
const slaTargets = {
  processing_time_minutes: 30,
  uptime_percent: 99.5,
  success_rate_percent: 95,
  response_time_ms: 5000
};

healthChecks.sla_compliance = {
  processing_time: {
    target: slaTargets.processing_time_minutes,
    actual: 22 + Math.random() * 15, // 22-37 minutes
    compliant: null
  },
  uptime: {
    target: slaTargets.uptime_percent,
    actual: 99.2 + Math.random() * 0.7, // 99.2-99.9%
    compliant: null
  },
  success_rate: {
    target: slaTargets.success_rate_percent,
    actual: 94 + Math.random() * 5, // 94-99%
    compliant: null
  },
  response_time: {
    target: slaTargets.response_time_ms,
    actual: 3000 + Math.random() * 4000, // 3-7 seconds
    compliant: null
  }
};

// Determine SLA compliance
Object.keys(healthChecks.sla_compliance).forEach(metric => {
  const sla = healthChecks.sla_compliance[metric];
  if (metric === 'processing_time' || metric === 'response_time') {
    sla.compliant = sla.actual <= sla.target;
  } else {
    sla.compliant = sla.actual >= sla.target;
  }
});

// Overall health score calculation
let healthScore = 100;

// Deduct for unhealthy endpoints
const unhealthyEndpoints = healthChecks.api_endpoints.filter(ep => ep.status === 'unhealthy').length;
healthScore -= unhealthyEndpoints * 15;

// Deduct for high error rates
if (healthChecks.error_rates.error_rate_percent > 5) {
  healthScore -= 20;
}

// Deduct for SLA violations
const slaViolations = Object.values(healthChecks.sla_compliance).filter(sla => !sla.compliant).length;
healthScore -= slaViolations * 10;

// Deduct for high resource usage
if (healthChecks.resource_usage.cpu_usage_percent > 80) {
  healthScore -= 10;
}
if (healthChecks.resource_usage.memory_usage_percent > 85) {
  healthScore -= 10;
}

healthScore = Math.max(0, healthScore);

// Determine alert level
let alertLevel = 'healthy';
if (healthScore < 50) {
  alertLevel = 'critical';
} else if (healthScore < 70) {
  alertLevel = 'warning';
} else if (healthScore < 85) {
  alertLevel = 'attention';
}

return [{
  json: {
    overall_health_score: healthScore,
    alert_level: alertLevel,
    timestamp: new Date().toISOString(),
    health_checks: healthChecks,
    recommendations: generateRecommendations(healthChecks, healthScore)
  }
}];

function generateRecommendations(checks, score) {
  const recommendations = [];

  if (score < 70) {
    recommendations.push('URGENT: System health below acceptable threshold');
  }

  const unhealthyEndpoints = checks.api_endpoints.filter(ep => ep.status === 'unhealthy');
  if (unhealthyEndpoints.length > 0) {
    recommendations.push(`Check API connectivity for: ${unhealthyEndpoints.map(ep => ep.name).join(', ')}`);
  }

  if (checks.error_rates.error_rate_percent > 5) {
    recommendations.push('High error rate detected - investigate recent changes');
  }

  const slaViolations = Object.entries(checks.sla_compliance)
    .filter(([_, sla]) => !sla.compliant)
    .map(([metric, _]) => metric);

  if (slaViolations.length > 0) {
    recommendations.push(`SLA violations in: ${slaViolations.join(', ')}`);
  }

  if (checks.resource_usage.cpu_usage_percent > 80) {
    recommendations.push('High CPU usage - consider scaling resources');
  }

  if (checks.resource_usage.memory_usage_percent > 85) {
    recommendations.push('High memory usage - check for memory leaks');
  }

  if (recommendations.length === 0) {
    recommendations.push('System operating within normal parameters');
  }

  return recommendations;
}

