// Intelligent Batch Processing Algorithm
const submissions = items.map(item => item.json);

if (submissions.length === 0) {
  return [{ json: { message: 'No submissions found for batch processing' } }];
}

// Group submissions by processing characteristics
const processingGroups = {
  high_priority_oncology: [],
  high_priority_other: [],
  medium_priority_complex: [],
  medium_priority_standard: [],
  low_priority: []
};

submissions.forEach(submission => {
  const priority = submission.priority_level;
  const therapeutic = submission.therapeutic_area;
  const complexity = calculateComplexity(submission);

  if (priority === 'high' && therapeutic === 'Oncology') {
    processingGroups.high_priority_oncology.push(submission);
  } else if (priority === 'high') {
    processingGroups.high_priority_other.push(submission);
  } else if (priority === 'medium' && complexity > 0.7) {
    processingGroups.medium_priority_complex.push(submission);
  } else if (priority === 'medium') {
    processingGroups.medium_priority_standard.push(submission);
  } else {
    processingGroups.low_priority.push(submission);
  }
});

// Create optimized batch configuration
const batchConfig = {
  batch_id: generateBatchId(),
  total_submissions: submissions.length,
  processing_groups: processingGroups,
  batch_strategy: determineBatchStrategy(processingGroups),
  resource_allocation: calculateResourceAllocation(processingGroups),
  estimated_processing_time: estimateProcessingTime(processingGroups),
  parallel_processing_limit: 10,
  created_at: new Date().toISOString()
};

// Create processing batches with optimal sizing
const processingBatches = [];

// Process high priority oncology first (batch size: 5)
if (processingGroups.high_priority_oncology.length > 0) {
  const batches = chunkArray(processingGroups.high_priority_oncology, 5);
  batches.forEach((batch, index) => {
    processingBatches.push({
      batch_number: processingBatches.length + 1,
      batch_type: 'high_priority_oncology',
      submissions: batch,
      processing_priority: 1,
      resource_allocation: 'high',
      ai_provider_preference: 'claude',
      estimated_time_minutes: batch.length * 8,
      parallel_limit: 3
    });
  });
}

// Process other high priority (batch size: 8)
if (processingGroups.high_priority_other.length > 0) {
  const batches = chunkArray(processingGroups.high_priority_other, 8);
  batches.forEach((batch, index) => {
    processingBatches.push({
      batch_number: processingBatches.length + 1,
      batch_type: 'high_priority_other',
      submissions: batch,
      processing_priority: 2,
      resource_allocation: 'high',
      ai_provider_preference: 'gpt4',
      estimated_time_minutes: batch.length * 6,
      parallel_limit: 4
    });
  });
}

// Process medium priority complex (batch size: 6)
if (processingGroups.medium_priority_complex.length > 0) {
  const batches = chunkArray(processingGroups.medium_priority_complex, 6);
  batches.forEach((batch, index) => {
    processingBatches.push({
      batch_number: processingBatches.length + 1,
      batch_type: 'medium_priority_complex',
      submissions: batch,
      processing_priority: 3,
      resource_allocation: 'medium',
      ai_provider_preference: 'claude',
      estimated_time_minutes: batch.length * 7,
      parallel_limit: 3
    });
  });
}

// Process medium priority standard (batch size: 10)
if (processingGroups.medium_priority_standard.length > 0) {
  const batches = chunkArray(processingGroups.medium_priority_standard, 10);
  batches.forEach((batch, index) => {
    processingBatches.push({
      batch_number: processingBatches.length + 1,
      batch_type: 'medium_priority_standard',
      submissions: batch,
      processing_priority: 4,
      resource_allocation: 'medium',
      ai_provider_preference: 'perplexity',
      estimated_time_minutes: batch.length * 4,
      parallel_limit: 5
    });
  });
}

// Process low priority (batch size: 15)
if (processingGroups.low_priority.length > 0) {
  const batches = chunkArray(processingGroups.low_priority, 15);
  batches.forEach((batch, index) => {
    processingBatches.push({
      batch_number: processingBatches.length + 1,
      batch_type: 'low_priority',
      submissions: batch,
      processing_priority: 5,
      resource_allocation: 'low',
      ai_provider_preference: 'perplexity',
      estimated_time_minutes: batch.length * 3,
      parallel_limit: 6
    });
  });
}

return [{
  json: {
    batch_configuration: batchConfig,
    processing_batches: processingBatches,
    execution_plan: {
      total_batches: processingBatches.length,
      estimated_total_time_minutes: processingBatches.reduce((sum, batch) => sum + batch.estimated_time_minutes, 0),
      resource_requirements: calculateTotalResourceRequirements(processingBatches),
      processing_order: processingBatches.map(batch => ({
        batch_number: batch.batch_number,
        type: batch.batch_type,
        count: batch.submissions.length,
        priority: batch.processing_priority
      }))
    }
  }
}];

function calculateComplexity(submission) {
  let complexity = 0.5; // base complexity

  if (submission.therapeutic_area === 'Oncology') complexity += 0.2;
  if (submission.therapeutic_area === 'Rare Disease') complexity += 0.3;
  if (submission.mechanism_of_action?.includes('novel')) complexity += 0.1;
  if (submission.development_stage === 'Phase III') complexity += 0.1;

  return Math.min(complexity, 1.0);
}

function generateBatchId() {
  return `BATCH-${new Date().toISOString().slice(0, 10)}-${Math.random().toString(36).substr(2, 6)}`;
}

function determineBatchStrategy(groups) {
  const totalSubmissions = Object.values(groups).reduce((sum, group) => sum + group.length, 0);

  if (totalSubmissions > 30) {
    return 'parallel_processing';
  } else if (totalSubmissions > 15) {
    return 'sequential_with_overlap';
  } else {
    return 'sequential';
  }
}

function calculateResourceAllocation(groups) {
  return {
    cpu_cores: Math.min(16, Math.max(4, Object.values(groups).reduce((sum, group) => sum + group.length, 0) / 5)),
    memory_gb: Math.min(32, Math.max(8, Object.values(groups).reduce((sum, group) => sum + group.length, 0) / 3)),
    api_rate_limit_buffer: 0.8, // Use 80% of rate limits
    concurrent_ai_calls: Math.min(10, Math.max(3, groups.high_priority_oncology.length + groups.high_priority_other.length))
  };
}

function estimateProcessingTime(groups) {
  const processingTimes = {
    high_priority_oncology: 8, // minutes per submission
    high_priority_other: 6,
    medium_priority_complex: 7,
    medium_priority_standard: 4,
    low_priority: 3
  };

  let totalTime = 0;
  Object.entries(groups).forEach(([groupName, submissions]) => {
    totalTime += submissions.length * processingTimes[groupName];
  });

  return {
    sequential_time_minutes: totalTime,
    parallel_time_minutes: Math.ceil(totalTime / 4), // Assuming 4x parallelization
    estimated_completion: new Date(Date.now() + (totalTime / 4) * 60000).toISOString()
  };
}

function calculateTotalResourceRequirements(batches) {
  return {
    total_api_calls: batches.reduce((sum, batch) => sum + batch.submissions.length * 3, 0), // 3 calls per submission
    peak_concurrent_processing: Math.max(...batches.map(b => b.parallel_limit)),
    estimated_cost_usd: batches.reduce((sum, batch) => {
      const costPerSubmission = {
        high_priority_oncology: 0.25,
        high_priority_other: 0.20,
        medium_priority_complex: 0.18,
        medium_priority_standard: 0.12,
        low_priority: 0.08
      };
      return sum + (batch.submissions.length * costPerSubmission[batch.batch_type]);
    }, 0)
  };
}

function chunkArray(array, chunkSize) {
  const chunks = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    chunks.push(array.slice(i, i + chunkSize));
  }
  return chunks;
}

