// Role-Based Approval Routing Engine
const submission = items[0].json;

// Define approval workflow based on submission characteristics
const approvalRules = {
  // High-risk pharmaceutical content requires all approvals
  high_risk: {
    required_approvals: ['seo_reviewer', 'medical_reviewer', 'legal_reviewer', 'mlr_reviewer'],
    approval_order: 'sequential',
    escalation_time_hours: 24,
    bypass_roles: ['admin', 'emergency_approver']
  },

  // Medium-risk content requires medical and legal
  medium_risk: {
    required_approvals: ['seo_reviewer', 'medical_reviewer', 'mlr_reviewer'],
    approval_order: 'parallel_then_sequential', // SEO + Medical parallel, then MLR
    escalation_time_hours: 48,
    bypass_roles: ['admin']
  },

  // Low-risk content requires basic approvals
  low_risk: {
    required_approvals: ['seo_reviewer', 'content_reviewer'],
    approval_order: 'parallel',
    escalation_time_hours: 72,
    bypass_roles: ['admin', 'senior_reviewer']
  }
};

// Risk assessment algorithm
function assessRisk(submission) {
  let riskScore = 0;

  // Therapeutic area risk
  const highRiskAreas = ['Oncology', 'Neurology', 'Rare Disease', 'Pediatrics'];
  if (highRiskAreas.includes(submission.therapeutic_area)) {
    riskScore += 30;
  }

  // Development stage risk
  if (submission.development_stage === 'Phase III' || submission.development_stage === 'Approved') {
    riskScore += 25;
  }

  // Content complexity risk
  if (submission.ai_generated_content && submission.ai_generated_content.length > 2000) {
    riskScore += 15;
  }

  // Medical claims risk
  const highRiskClaims = ['first-in-class', 'breakthrough', 'superior efficacy', 'novel mechanism'];
  const contentLower = (submission.ai_generated_content || '').toLowerCase();
  const hasHighRiskClaims = highRiskClaims.some(claim => contentLower.includes(claim));
  if (hasHighRiskClaims) {
    riskScore += 20;
  }

  // Regulatory status risk
  if (submission.regulatory_status === 'pending' || submission.regulatory_status === 'investigational') {
    riskScore += 10;
  }

  // Determine risk level
  if (riskScore >= 70) return 'high_risk';
  if (riskScore >= 40) return 'medium_risk';
  return 'low_risk';
}

// Get user roles and permissions
function getUserRoles() {
  // In production, this would query the database
  return {
    'john.doe@pharma.com': ['seo_reviewer', 'senior_reviewer'],
    'jane.smith@pharma.com': ['medical_reviewer', 'admin'],
    'bob.johnson@pharma.com': ['legal_reviewer'],
    'mary.wilson@pharma.com': ['mlr_reviewer'],
    'admin@pharma.com': ['admin', 'emergency_approver']
  };
}

// Create approval workflow
const riskLevel = assessRisk(submission);
const approvalConfig = approvalRules[riskLevel];
const userRoles = getUserRoles();

// Generate approval stages
const approvalStages = [];
const requiredRoles = approvalConfig.required_approvals;

if (approvalConfig.approval_order === 'sequential') {
  // Sequential approval - each role one after another
  requiredRoles.forEach((role, index) => {
    approvalStages.push({
      stage_number: index + 1,
      stage_name: `${role}_approval`,
      required_role: role,
      parallel_approvers: 1,
      depends_on_stages: index > 0 ? [index] : [],
      escalation_time_hours: approvalConfig.escalation_time_hours,
      status: 'pending'
    });
  });
} else if (approvalConfig.approval_order === 'parallel') {
  // Parallel approval - all roles simultaneously
  approvalStages.push({
    stage_number: 1,
    stage_name: 'parallel_approval',
    required_roles: requiredRoles,
    parallel_approvers: requiredRoles.length,
    depends_on_stages: [],
    escalation_time_hours: approvalConfig.escalation_time_hours,
    status: 'pending'
  });
} else if (approvalConfig.approval_order === 'parallel_then_sequential') {
  // Mixed: First two parallel, then sequential
  approvalStages.push({
    stage_number: 1,
    stage_name: 'initial_parallel_approval',
    required_roles: requiredRoles.slice(0, 2),
    parallel_approvers: 2,
    depends_on_stages: [],
    escalation_time_hours: approvalConfig.escalation_time_hours / 2,
    status: 'pending'
  });

  requiredRoles.slice(2).forEach((role, index) => {
    approvalStages.push({
      stage_number: index + 2,
      stage_name: `${role}_approval`,
      required_role: role,
      parallel_approvers: 1,
      depends_on_stages: [1],
      escalation_time_hours: approvalConfig.escalation_time_hours / 2,
      status: 'pending'
    });
  });
}

// Assign specific approvers based on roles and availability
const approverAssignments = {};
requiredRoles.forEach(role => {
  const availableUsers = Object.entries(userRoles)
    .filter(([user, roles]) => roles.includes(role))
    .map(([user, roles]) => user);

  if (availableUsers.length > 0) {
    approverAssignments[role] = {
      primary_approver: availableUsers[0],
      backup_approvers: availableUsers.slice(1),
      role_name: role
    };
  }
});

// Create approval workflow record
const approvalWorkflow = {
  workflow_id: `APR-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`,
  submission_id: submission.id,
  risk_level: riskLevel,
  risk_score: assessRisk(submission),
  approval_config: approvalConfig,
  approval_stages: approvalStages,
  approver_assignments: approverAssignments,
  workflow_status: 'active',
  current_stage: 1,
  created_at: new Date().toISOString(),
  estimated_completion: new Date(Date.now() + (approvalConfig.escalation_time_hours * 60 * 60 * 1000)).toISOString(),

  // Escalation rules
  escalation_rules: {
    auto_escalate_after_hours: approvalConfig.escalation_time_hours,
    escalation_chain: ['senior_reviewer', 'admin', 'emergency_approver'],
    weekend_escalation: true,
    holiday_escalation: false
  },

  // Override mechanisms
  override_permissions: {
    emergency_override_roles: approvalConfig.bypass_roles,
    requires_justification: true,
    audit_trail_required: true
  },

  // SLA tracking
  sla_metrics: {
    target_completion_hours: approvalConfig.escalation_time_hours * 0.8, // 80% of escalation time
    priority_multiplier: riskLevel === 'high_risk' ? 1.5 : riskLevel === 'medium_risk' ? 1.2 : 1.0,
    business_hours_only: false
  }
};

return [{
  json: {
    approval_workflow: approvalWorkflow,
    next_action: 'initiate_first_stage',
    notifications_to_send: generateInitialNotifications(approvalWorkflow),
    monitoring_setup: {
      check_interval_minutes: 30,
      escalation_warnings: [50, 75, 90], // Percentage of time elapsed
      stakeholder_updates: ['daily_summary', 'stage_completion', 'escalation_triggered']
    }
  }
}];

function generateInitialNotifications(workflow) {
  const notifications = [];

  // Notify assigned approvers for first stage
  const firstStage = workflow.approval_stages[0];
  if (firstStage.required_role) {
    const assignment = workflow.approver_assignments[firstStage.required_role];
    if (assignment) {
      notifications.push({
        type: 'approval_request',
        recipient: assignment.primary_approver,
        role: firstStage.required_role,
        stage: 1,
        deadline: new Date(Date.now() + (firstStage.escalation_time_hours * 60 * 60 * 1000)).toISOString()
      });
    }
  } else if (firstStage.required_roles) {
    // Multiple parallel approvers
    firstStage.required_roles.forEach(role => {
      const assignment = workflow.approver_assignments[role];
      if (assignment) {
        notifications.push({
          type: 'approval_request',
          recipient: assignment.primary_approver,
          role: role,
          stage: 1,
          deadline: new Date(Date.now() + (firstStage.escalation_time_hours * 60 * 60 * 1000)).toISOString()
        });
      }
    });
  }

  // Notify submission owner
  notifications.push({
    type: 'approval_initiated',
    recipient: workflow.approver_assignments.submitter_email || 'unknown@pharma.com',
    workflow_id: workflow.workflow_id,
    estimated_completion: workflow.estimated_completion
  });

  return notifications;
}
